import React, { Component } from 'react';

class Summary extends Component {
  constructor(props) {
    super(props);
    this.state = {
      months: [
        'January', 'February', 'January', 'February',
        'January', 'February', 'January', 'February',
        'January', 'February','January', 'February',
      ],
      data: [],
    };
   }

  componentWillReceiveProps(nextProps) {
    console.log(nextProps);
    console.log(this.props.years);
  }

  monthlyPayment = (price, deposit, years) => {
    const priceAfterDeposit = price - deposit;
    const totalMonths = years * 12;
    return (priceAfterDeposit/totalMonths).toFixed(2);
  };

  render() {
    return (
      <div className="summary">
      { this.monthlyPayment(this.props.price, this.props.deposit, this.props.years) }
      </div>
    );
  }
}

export default Summary;
