import React, { Component } from 'react';
import Search from './../search/search';
import Summary from './../summary/summary';
import AffordableCars from './../affordableCars/affordableCars';
import addDays from "date-fns/addDays";
import "./main.scss";
import carImage from './../../../assets/car.png';

class Main extends Component {
  constructor(props) {
    super(props);
    this.reference = React.createRef();
    this.state = {
      price: 0,
      deposit: 0,
      years: 1,
      deliveryDate: addDays(new Date(), 1),
    };

    this.handleSearches = this.handleSearches.bind(this);
  }

  scrollToreference = () => {
    window.scrollTo({
        top: this.reference.offsetTop - 100, 
        behavior: "smooth"
    });
  };

  handleSearches = (search) => {
    this.setState({...search});
    this.scrollToreference();
  }

  render = () => {
    return (
      <main role="main" className="main">
        <header className="jumbotron">
            <div className="container">
                <div className="row flex-column-reverse flex-md-row">
                  <div className="col-md-5">
                    <Search searches={this.handleSearches}></Search>
                  </div>
                  <div className="col-md-7">
                    <h1>We introduce a new interest free car loan</h1>
                    <img src={carImage} alt="Car Logo"/>
                  </div>
                </div>
            </div>
          </header>
          <div ref={ (ref) => this.reference=ref }>
            <Summary 
              price={this.state.price}
              deposit={this.state.deposit}
              years={this.state.years}
              deliveryDate={this.state.deliveryDate}>
            </Summary>
          </div>
          <AffordableCars
            price={this.state.price}
            deposit={this.state.deposit}
            years={this.state.years}
          ></AffordableCars>
      </main>
    );
  }
}

export default Main;
