import React, { Component } from 'react';
import Loader from 'react-loader-spinner';
import searchInterestFreeCars from './../../../services/searchInterestFreeCars';
import missingCarIcon from './../../../assets/car-icon';

class AffordableCars extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: false,
      sentRequest: false,
      data: {
        searchResults: []
      }
    };
   }

  componentWillReceiveProps(newProps) {
    const monthlyRepayment = this.monthlyRepayment(newProps.price, newProps.deposit, newProps.years);
    this.searchingCar(monthlyRepayment);
  }

  monthlyRepayment = (price, deposit, years) => {
    const priceAfterDeposit = this.priceAfterDeposit(price, deposit);
    const totalMonths = years * 12;
    return parseFloat((priceAfterDeposit/totalMonths).toFixed(2));
  };

  priceAfterDeposit = (price, deposit) => {
    return price - deposit;
  };

  searchingCar = async (price) => {
    this.setState({ loading: true, sentRequest: true });
    await searchInterestFreeCars(price).then((response)=>{
      console.log(response);
      this.setState({ data: response });
      this.setState({ loading: false });
   }) 
  }

  render() {
    // const cars = () => {
    //   if (this.state.loading) return <Loader type="Oval" color="#2C3637"/> 
    //   else if (Object.keys(this.state.data).length === 0
    //     || this.state.data.searchResults.length < 1) return <h1>No Results Found.</h1>
    //   const searchedCars = this.state.data.searchResults.slice(0, 6).map((item) =>
    //     <div className="card">

    //     </div>
    //   );
    //   return searchedCars;
    // }

    const cars = <div>
      {
        this.state.loading
        ? <Loader type="Oval" color="#2C3637"/>
        : this.state.data.searchResults.length < 1 
        ? this.state.sentRequest 
          ? <h1>No Results Found.</h1> 
          : ''
        : this.state.data.searchResults.slice(0, 6).map((item, index) =>
            <div key={index} className="card">
              {
                item.photos.length > 0
                ? <img className="card-img-top" src={item.photos[0]} alt="Card image cap"/>
                : <img className="card-img-top" src={item.photos[0]} alt="Card image cap"/>
              }
              <div className="card-body">
                <h5 className="card-title">Card title</h5>
                <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                <a href="#" className="btn btn-primary">Go somewhere</a>
              </div>
            </div>
          )
      }
    </div>

    return (
      <section className="py-5 affordable-cars">
        <div className="container">
          {cars}
        </div> 
      </section>
    );
  }
}

export default AffordableCars;
