import React, { Component } from 'react';
// import scrollToComponent from 'react-scroll-to-component';
import Search from '../search/search';
import Summary from '../summary/summary';
import searchInterestFreeCars from '../../../services/searchInterestFreeCars';
import addDays from "date-fns/addDays";
import "./main.scss";
let scrollToComponent = null;
class Main extends Component {
  constructor(props) {
    super(props);
    this.state = {
      price: 0,
      deposit: 0,
      years: 1,
      deliveryDate: addDays(new Date(), 1),
    };

    scrollToComponent(this.refs.name, {
      offset: 1000,
      align: 'top',
      duration: 1500
    });

    this.handleSearches = this.handleSearches.bind(this);
  }
  async componentWillMount() {
    scrollToComponent = await import('react-scroll-to-component');
  }

  handleSearches = (search) => {
    this.setState({...search});
    scrollToComponent(this.Summary, { offset: -100, align: 'top', duration: 500, ease:'inCirc'});
    this.searching();
  }
  searching = async () => {
    await searchInterestFreeCars(this.state.price).then((response)=>{
      console.log(response);
   }) 
  }
  render() {
    return (
      <main role="main" className="main">
        <header className="jumbotron">
            <div className="container">
                <div className="row flex-column-reverse flex-md-row">
                  <div className="col-md-5">
                    <Search searches={this.handleSearches}></Search>
                  </div>
                  <div className="col-md-7">
                    dcsdc
                  </div>
                </div>
            </div>
          </header>
          <Summary 
            ref={(section) => { this.Summary = section; }}
            price={this.state.price}
            deposit={this.state.deposit}
            years={this.state.years}
            deliveryDate={this.state.deliveryDate}>
          </Summary>
      </main>
    );
  }
}

export default Main;
