import React, { Component } from 'react';
import Moment from 'moment';

class Summary extends Component {
  constructor(props) {
    super(props);
    this.state = {
      arrangementFee: 88,
      completionFee: 20,
      data: [],
    };
   }

  componentWillReceiveProps(newProps) {
    this.populateSummaryData(
      newProps.price, newProps.deposit,
      newProps.years, newProps.deliveryDate
    );

  }

  populateSummaryData = (price, deposit, years, date) => {
    const monthsRequired = years * 12;
    for (let i = 1; i <= monthsRequired; i++) {
      this.state.data.push(
        {
          month: Moment(date).add(i, 'months').format('MMMM YYYY'),
          repayment: this.monthlyPayment(price, deposit, years),
          arrangementFee: i === 1 ? this.state.arrangementFee : 0,
          completionFee: i === monthsRequired ? this.state.completionFee: 0,
        }
      )
    }

    console.log(this.state.data)
  }

  monthlyPayment = (price, deposit, years) => {
    const priceAfterDeposit = price - deposit;
    const totalMonths = years * 12;
    console.log((priceAfterDeposit/totalMonths).toFixed(2))
    return (priceAfterDeposit/totalMonths).toFixed(2);
  };

  render() {
    return (
      <div className="summary">

      </div>
    );
  }
}

export default Summary;
