import React, { Component } from 'react';
import { Form, InputGroup, Button } from 'react-bootstrap';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import addDays from "date-fns/addDays";
class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      price: 0,
      deposit: 0,
      year: 1,
      deliveryDate: addDays(new Date(), 1),
      priceError: false,
      depositError: false,
    };

    this.handleYearChange = this.handleYearChange.bind(this);
    this.handlePriceChange = this.handlePriceChange.bind(this);
    this.handleDepositChange = this.handleDepositChange.bind(this);
    this.handleDateChange = this.handleDateChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
   }

  handleYearChange = (evt) => this.setState({ year: evt.target.value });
  handlePriceChange = (evt) => this.setState({ price: evt.target.value });
  handleDepositChange = (evt) => this.setState({ deposit: evt.target.value });
  handleDateChange = (date) => {
    this.setState({
      deliveryDate: date
    });
  };
  handleSubmit = (event) => {
    this.state.price === 0
    ? this.setState({priceError: true})
    : this.setState({priceError: false});
    console.log(((this.state.deposit/this.state.price)*100));
    this.state.deposit === 0 
    || ((this.state.deposit/this.state.price)*100) === NaN
    || ((this.state.deposit/this.state.price)*100) < 15
    ? this.setState({priceError: true})
    : this.setState({priceError: false});
    event.preventDefault();
  }

  render() {
    console.log(this.state);
    return (
      <div className="search">
        <Form>
          <Form.Group>
            <Form.Label>Years</Form.Label>
            <Form.Control as="select" 
              value={this.state.year}
              onChange={this.handleYearChange}>
              <option value="1">1</option>
              <option value="2">2</option>
              <option value="3">3</option>
            </Form.Control>
          </Form.Group>
          <Form.Group>
            <Form.Label>Price</Form.Label>
            <InputGroup>
              <InputGroup.Prepend>
                <InputGroup.Text><b>£</b></InputGroup.Text>
              </InputGroup.Prepend>
              <Form.Control
                type="number"
                placeholder="Username"
                aria-describedby="inputGroupPrepend"
                required
                onChange={this.handlePriceChange}
              />
            </InputGroup>
            <div className="invalid-feedback d-block">
              {this.state.priceError ? 'currently' : ''}
            </div>
          </Form.Group>

          <Form.Group>
            <Form.Label>Deposit</Form.Label>
            <InputGroup>
              <InputGroup.Prepend>
                <InputGroup.Text><b>£</b></InputGroup.Text>
              </InputGroup.Prepend>
              <Form.Control
                type="number"
                placeholder="Username"
                aria-describedby="inputGroupPrepend"
                required
                onChange={this.handleDepositChange}
              />
            </InputGroup>
            <div className="invalid-feedback d-block">
              {this.state.depositError ? 'currently' : ''}
            </div>
          </Form.Group>

          <Form.Group>
            <DatePicker
              dateFormat="dd/MM/YYYY"
              minDate={addDays(new Date(), 1)}
              selected={this.state.deliveryDate}
              onChange={this.handleDateChange}
            />
          </Form.Group>
          <Button variant="primary" onClick={this.handleSubmit}>
            Submit
          </Button>
        </Form>
      </div>
    );
  }
}

export default Search;
