import React, { Component } from 'react';
import Moment from 'moment';
import "./summary.scss";

class Summary extends Component {
  constructor(props) {
    super(props);
    this.state = {
      arrangementFee: 88.00,
      completionFee: 20.00,
      data: [],
    };
   }

  componentWillReceiveProps(newProps) {
    this.populateSummaryData(
      newProps.price, newProps.deposit,
      newProps.years, newProps.deliveryDate
    );
  }

  clearData = () => {
    this.setState({ data: [] });
  };

  populateSummaryData = (price, deposit, years, date) => {
    const monthsRequired = years * 12;
    this.clearData();
    for (let i = 1; i <= monthsRequired; i++) {
      this.setState(state => {
        state.data.push(
          {
            month: Moment(date).add(i, 'months').startOf('month').add(6 - Moment().day("Monday").day() ,'days').startOf('week').day(1).format("Do MMMM YYYY"),
            repayment: parseFloat(this.monthlyRepayment(price, deposit, years)),
            arrangementFee: i === 1 ? parseFloat(this.state.arrangementFee) : 0,
            completionFee: i === monthsRequired ? parseFloat(this.state.completionFee): 0,
          }
        )
      });
    }
  };

  monthlyRepayment = (price, deposit, years) => {
    const priceAfterDeposit = this.priceAfterDeposit(price, deposit);
    const totalMonths = years * 12;
    return (priceAfterDeposit/totalMonths).toFixed(2);
  };

  priceAfterDeposit = (price, deposit) => {
    return price - deposit;
  };

  render() {
    const populateMonthlyRepayments = this.state.data.map((item, index) => 
      <div key={index}>
        <div className="card bg-light mb-3">
          <div className="card-header">
            <h4 className="card-title">{item.month}</h4>
          </div>
          <div className="card-body">
            <div>
              <span>Installement: </span>
              <b className="card-text">£{(item.repayment).toFixed(2)}</b>
            </div>
            { item.arrangementFee !== 0 ?
              <div>
                <span>Arrangement Fee: </span>
                <b className="card-text">£{(item.arrangementFee).toFixed(2)}</b>
              </div>
              : ''
            }
            { item.completionFee !== 0 ?
              <div>
                <span>Completion Fee: </span>
                <b className="card-text">£{(item.completionFee).toFixed(2)}</b>
              </div>
              : ''
            }
            { item.arrangementFee === 0 && item.completionFee === 0 ?
              <div>
                <span className="card-text">No Extra Charges</span>
              </div>
              : ''
            }
          </div>
          <div class="card-footer">
            <div>
              <span>Total: </span>
              <h5>
                £{(item.repayment + item.arrangementFee + item.completionFee).toFixed(2)}
              </h5>
            </div>
          </div>
        </div>
      </div>
    );
    return (
      <section className="py-5 summary">
        { this.state.data.length > 0 ?
          <div className="container">
          <h1>Qoute</h1>
          <hr/>
          <div className="row">
            <div className="col-md-6">
              <b>Monthly Repayments</b>
              <div className="repayments">
                {populateMonthlyRepayments}
              </div>
            </div>
            <div className="col-md-6">
              <h3>Summary</h3>
              <div>
                <span>No. Monthly Repayments: </span>
                <b>{this.props.years * 12}</b>
              </div>
            </div>
          </div>
          </div>
          : <div className="container">
              <h1>Please enter the details to search the qoute.</h1>
            </div>
        }
      </section>
    );
  }
}

export default Summary;
