import React, { Component } from 'react';
import Loader from 'react-loader-spinner';
import searchInterestFreeCars from './../../../services/searchInterestFreeCars';
import missingCarIcon from './../../../assets/car-icon.png';
import "./affordableCars.scss";

class AffordableCars extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: false,
      sentRequest: false,
      data: {
        searchResults: []
      },
    };
   }

  componentWillReceiveProps = (newProps) => {
    const monthlyRepayment = this.monthlyRepayment(newProps.price, newProps.deposit, newProps.years);
    this.searchingCar(monthlyRepayment);
  };

  monthlyRepayment = (price, deposit, years) => {
    const priceAfterDeposit = this.priceAfterDeposit(price, deposit);
    const totalMonths = years * 12;
    return parseFloat((priceAfterDeposit/totalMonths).toFixed(2));
  };

  priceAfterDeposit = (price, deposit) => {
    return price - deposit;
  };

  searchingCar = async (price) => {
    this.setState({ loading: true, sentRequest: true });
    await searchInterestFreeCars(price).then((response)=>{
      this.setState({ data: response });
      this.setState({ loading: false });
    });
  };

  render = () => {
    const filteredCars = this.state.data.searchResults.filter(item => item.isReserved === false);
    const carResults = 
      <div className="content">
        <div className="row">
          {filteredCars.slice(0, 6).map((item, index) => 
            <div key={index} className="col-lg-4">
                <div className="card">
                  {
                    item.photos.length > 0
                    ? <img className="card-img-top" src={item.photos[0]} alt="Car"/>
                    : <img className="card-img-top" src={missingCarIcon} alt="Car"/>
                  }
                  <div className="card-body">
                    <h5 className="card-title">{item.title.name}</h5>
                    <ul className="card-text">
                      {
                        item.salesInfo.summary.slice(0, 7).map((item, index) =>
                          <li key={index}>{item}</li>
                        )
                      }
                    </ul>
                    {
                      item.salesInfo.pricing.monthlyPayment
                      ? <h4>Â£{item.salesInfo.pricing.monthlyPayment}/month</h4>
                      : ''
                    }
                    <a href={`https://www.arnoldclark.com${item.url}`} target="_blank" rel="noopener noreferrer" className="btn btn-large btn-block btn-success"><b>Find More</b></a>
                  </div>
                </div>
              </div>
            )}
        </div>
      </div>

    return (
      <section className="py-5 affordable-cars">
        <div className="container">
          {
            this.state.loading
            ? <div className="content"><Loader type="Oval" color="#2C3637"/></div>
            : ''
          }
          {
            !this.state.loading 
            && this.state.sentRequest 
            ? <div><h3>Affordable Cars</h3><hr/></div>
            : ''
          }
          {
            !this.state.loading 
            && this.state.sentRequest 
            && this.state.data.searchResults.length < 1
            ? <h1>No Results Found.</h1> 
            : ''
          }
          {
            !this.state.loading 
            && this.state.sentRequest 
            && this.state.data.searchResults.length > 1
            ? carResults
            : ''
          }
        </div> 
      </section>
    );
  }
}

export default AffordableCars;
